package android.coding.ourapp.adapterimport android.coding.ourapp.data.datasource.model.Reportimport android.coding.ourapp.databinding.ListItemMonthBindingimport android.coding.ourapp.utils.Utilsimport android.content.Contextimport android.graphics.Bitmapimport android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.recyclerview.widget.RecyclerViewimport java.util.ArrayListclass MonthAdapter(private val context : Context,private var listReport : MutableList<Report>) : RecyclerView.Adapter<MonthAdapter.MyViewHolder>() {    inner class MyViewHolder(val binding: ListItemMonthBinding): RecyclerView.ViewHolder(binding.root)    override fun onCreateViewHolder(        parent: ViewGroup,        viewType: Int    ): MyViewHolder {        return MyViewHolder(ListItemMonthBinding.inflate(LayoutInflater.from(parent.context),parent,false))    }    override fun onBindViewHolder(holder: MyViewHolder, position: Int) {        holder.binding.apply {            tvDate.text = listReport[position].reportName            val listBitmaps = mutableListOf<Bitmap>()            if(listReport[position].images.isNotEmpty()){                for(i in listReport[position].images){                    listBitmaps.add(Utils.convertStringToBitmap(i))                }            }            if(listBitmaps.isNotEmpty()){                Utils.showImageReportDetails(true, ArrayList(listBitmaps),null,this, context)            }        }    }    override fun getItemCount(): Int = listReport.size    fun getData(): List<Report> = listReport}