package android.coding.ourapp.helperimport android.coding.ourapp.data.datasource.model.Studentimport androidx.recyclerview.widget.DiffUtilclass StudentDiffCallback(    private val mOldStudentList: List<Student>,    private val mNewStudentList: List<Student>) : DiffUtil.Callback() {    override fun getOldListSize(): Int {        return mOldStudentList.size    }    override fun getNewListSize(): Int {        return mNewStudentList.size    }    override fun areItemsTheSame(oldItemPosition: Int, newItemPosition: Int): Boolean {        return mOldStudentList[oldItemPosition].id == mNewStudentList[newItemPosition].id    }    override fun areContentsTheSame(oldItemPosition: Int, newItemPosition: Int): Boolean {        val oldEmployee = mOldStudentList[oldItemPosition]        val newEmployee = mNewStudentList[newItemPosition]        return oldEmployee.nameStudent == newEmployee.nameStudent && oldEmployee.company == newEmployee.company && oldEmployee.group == newEmployee.group    }}