package android.coding.ourapp.presentation.ui.componentimport android.coding.ourapp.Rimport android.coding.ourapp.databinding.AlertComponentBindingimport android.content.Contextimport android.graphics.Colorimport android.graphics.drawable.ColorDrawableimport android.view.LayoutInflaterimport android.view.Viewimport androidx.appcompat.app.AlertDialogclass CustomAlertDialog(context: Context) {    private lateinit var binding: AlertComponentBinding    private val dialog: AlertDialog    init {        val dialogView = LayoutInflater.from(context).inflate(R.layout.alert_component, null)        val alertDialogBuilder = AlertDialog.Builder(context)            .setView(dialogView)        dialog = alertDialogBuilder.create()        // Atur properti tampilan dialog sesuai kebutuhan        dialog.window?.setBackgroundDrawable(ColorDrawable(Color.TRANSPARENT))        dialog.setCancelable(false)        // ...    }    fun show() {        dialog.show()    }    fun dismiss() {        dialog.dismiss()    }    // Contoh fungsi untuk mengatur judul pada dialog    fun setTitle(title: String) {        binding.tvTittle.text = title    }    // Contoh fungsi untuk mengatur pesan pada dialog    fun setMessage(message: String) {        binding.tvSubTittle.text = message    }    // Contoh fungsi untuk mengatur aksi tombol pada dialog    fun setPositiveButton(text: String, listener: View.OnClickListener) {        binding.btnYes.text = text        binding.btnYes.setOnClickListener(listener)    }    // Contoh fungsi untuk mengatur aksi tombol pada dialog    fun setNegativeButton(text: String, listener: View.OnClickListener) {        binding.btnCancel.text = text        binding.btnCancel.setOnClickListener(listener)    }    // ...}