package android.coding.ourapp.presentation.viewmodel.report_monthimport android.coding.ourapp.data.datasource.model.DataReportimport android.coding.ourapp.data.datasource.model.ReportMonthimport android.coding.ourapp.data.datasource.model.Studentimport android.coding.ourapp.data.repository.report_month.ReportMonthRepositoryimport androidx.lifecycle.ViewModelclass ReportMonthViewModel(private val reportMonthRepository: ReportMonthRepository) : ViewModel() {    fun addData(        nameStudent: String,        month: String,        point: String,        category: String,        desc: String,        onComplete: () -> Unit,        onFailure: (String) -> Unit    ) {        reportMonthRepository.addData(nameStudent, month, point, category, desc, onComplete, onFailure)    }    fun updateData(reportMonth: DataReport) {        reportMonthRepository.updateData(reportMonth)    }}