package android.coding.ourapp.presentation.viewmodel.studentimport android.coding.ourapp.data.Resourceimport android.coding.ourapp.data.datasource.model.Studentimport android.coding.ourapp.data.repository.student.StudentRepositoryimport androidx.lifecycle.LiveDataimport androidx.lifecycle.ViewModelclass StudentViewModel(private val studentRepository: StudentRepository) : ViewModel() {    private val studentLiveData: LiveData<Resource<List<Student>>> = studentRepository.getData()    fun getData(): LiveData<Resource<List<Student>>> {        return studentLiveData    }    fun addData(nameStudent: String, nameSchool: String, group: String,onComplete: () -> Unit, onFailure: (String) -> Unit) {        studentRepository.addData(nameStudent, nameSchool, group, onComplete, onFailure)    }    fun deleteData(student: Student){        studentRepository.deleteData(student)    }}